	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(QuestionPeer::DATABASE_NAME);

		if ($this->isColumnModified(QuestionPeer::ID)) $criteria->add(QuestionPeer::ID, $this->id);
		if ($this->isColumnModified(QuestionPeer::APP_ID)) $criteria->add(QuestionPeer::APP_ID, $this->app_id);
		if ($this->isColumnModified(QuestionPeer::SOCIAL_USER_ID)) $criteria->add(QuestionPeer::SOCIAL_USER_ID, $this->social_user_id);
		if ($this->isColumnModified(QuestionPeer::TITLE)) $criteria->add(QuestionPeer::TITLE, $this->title);
		if ($this->isColumnModified(QuestionPeer::TEXT)) $criteria->add(QuestionPeer::TEXT, $this->text);
		if ($this->isColumnModified(QuestionPeer::NUMBER_OF_LIKES)) $criteria->add(QuestionPeer::NUMBER_OF_LIKES, $this->number_of_likes);
		if ($this->isColumnModified(QuestionPeer::ANSWER_KIND)) $criteria->add(QuestionPeer::ANSWER_KIND, $this->answer_kind);
		if ($this->isColumnModified(QuestionPeer::ANSWER_ID)) $criteria->add(QuestionPeer::ANSWER_ID, $this->answer_id);
		if ($this->isColumnModified(QuestionPeer::DEL_FLAG)) $criteria->add(QuestionPeer::DEL_FLAG, $this->del_flag);
		if ($this->isColumnModified(QuestionPeer::ANSWERED_AT)) $criteria->add(QuestionPeer::ANSWERED_AT, $this->answered_at);
		if ($this->isColumnModified(QuestionPeer::CREATED_AT)) $criteria->add(QuestionPeer::CREATED_AT, $this->created_at);
		if ($this->isColumnModified(QuestionPeer::UPDATED_AT)) $criteria->add(QuestionPeer::UPDATED_AT, $this->updated_at);

		return $criteria;
	}

